
export const initializeServer = (server, port) => new Promise(resolve => server.listen(port, resolve))

export const initializeDatabase = async (databaseUrl) => {
    let connectDB;
    try {
        ({ connectDB } = require('@triframe/scribe'))
    } catch {
        throw Error(`Provided databaseUrl '${databaseUrl}' but '@triframe/scribe' could not be found (Install it, or don't pass databaseUrl to serve)`)
    }
    return await connectDB(databaseUrl)
}