import React from 'react';
import { Provider as PaperProvider, Snackbar, Portal, DefaultTheme } from './paper'
import { View } from 'react-native'
import { tether, Provider as TriFrameProvider } from '@triframe/arbiter-react'
import AsyncStorage from '@react-native-community/async-storage';

export { tether };

export const Provider = ({ theme, url, children }) => (
    <TriFrameProvider url={url} storage={AsyncStorage} >
        <PaperProvider theme={theme}>
            <View><Portal.Host /></View>
            {children}
        </PaperProvider>
    </TriFrameProvider>
)