import React from 'react'
import { Button, Area } from '.'

const Tab = ({ children, icon, selected = false, onPress }) => {
    return (
        <Button
            icon={icon}
            style={{
                flex: 1,
                borderStyle: 'solid',
                borderWidth: selected.tab !== 'events' ? 0.5 : 0,
                borderColor: 'rgba(0, 0, 0, 0.12)',
                backgroundColor: !selected ? 'rgba(0, 0, 0, 0)' : 'rgba(0,0,0,0.12)'
            }}
            onPress={onPress}
            mode="text"

        >
            {children}
        </Button>
    )
}

Tab.List = ({ children }) => {
    return <Area inline alignX="justify">{children}</Area>
}

export { Tab }