import * as React from 'react';
export const RadioButtonContext = React.createContext(null);
/**
 * Radio button group allows to control a group of radio buttons.
 *
 * ## Usage
 * ```js
 * import * as React from 'react';
 * import { View } from 'react-native';
 * import { RadioButton, Text } from '../../';
 *
 * export default class MyComponent extends React.Component {
 *   state = {
 *     value: 'first',
 *   };
 *
 *   render() {
 *     return(
 *       <RadioButton.Group
 *         onValueChange={value => this.setState({ value })}
 *         value={this.state.value}
 *       >
 *         <View>
 *           <Text>First</Text>
 *           <RadioButton value="first" />
 *         </View>
 *         <View>
 *           <Text>Second</Text>
 *           <RadioButton value="second" />
 *         </View>
 *       </RadioButton.Group>
 *     )
 *   }
 * }
 *```
 */
let RadioButtonGroup = /** @class */ (() => {
    class RadioButtonGroup extends React.Component {
        render() {
            const { value, onValueChange, children } = this.props;
            return (<RadioButtonContext.Provider value={{ value, onValueChange }}>
        {children}
      </RadioButtonContext.Provider>);
        }
    }
    RadioButtonGroup.displayName = 'RadioButton.Group';
    return RadioButtonGroup;
})();
export default RadioButtonGroup;
