import { prompt, getState } from "../../utils"

export const GoogleCloud = {

    requiresRelease: true,

    dependencyCheck: async () => {

    },
    
    getOptions: async () => {
        let { name } = await getState()

        let gcProjectName = await prompt(`What would you like to name the Google Cloud project? A new project with this name will be created if none already exists (${name})`)
        let gcRegion = await prompt(`What region would you like to deploy to? (us-central1)`)

        if(gcProjectName == '') gcProjectName = name
        if(gcRegion == '') gcRegion = 'us-central1'

        return { gcProjectName, gcRegion }
    }

}