import { getState, print, saveState } from "../../utils"

export default async (name) => {
    let state = await getState()
    let { deployments  = {}} = state
    if(deployments[name] == undefined) return print(`No such deployment '${name}'`)
    delete deployments[name]
    await saveState(state)
    print(`Deployment '${name}' has been removed`)
}