#!/usr/bin/env node
import builds from './commands/build'
import deployments from './commands/deployment'
import deploy from './commands/deploy/index.js'
import start from './commands/start'
import dotenv from 'dotenv';
dotenv.config();

const mason = require('commander')

let deployment = (
    mason
        .command('deployment')
)

deployment
    .command('add <name> [target]')
    .action(deployments.add)

deployment
    .command('remove <name>')
    .action(deployments.remove)

mason
    .command('deploy <deployment>')
    .action(deploy)



let build = (
    mason
        .command('build')
)

build
    .command('form [...args]')
    .action(builds.form)

build
    .command('model [...args]')
    .action(builds.model)

build
    .command('view [...args]')
    .action(builds.view)

mason
    .command('start')
    .action(start)


mason.parse(process.argv)